import React, { Component, useState, useEffect } from "react";
import { useHistory } from 'react-router-dom';
import axios from "axios";
import AppBar from "@material-ui/core/AppBar";
import Button from "@material-ui/core/Button";
import Box from '@material-ui/core/Box';
import CameraIcon from "@material-ui/icons/PhotoCamera";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import CssBaseline from "@material-ui/core/CssBaseline";
import Grid from "@material-ui/core/Grid";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import Link from "@material-ui/core/Link";
import Pagination from '@material-ui/lab/Pagination';
// Imports for upload
import Autocomplete from "@material-ui/lab/Autocomplete";
import TextField from "@material-ui/core/TextField";
import { FileDrop } from "react-file-drop";
import "./dragdrop.css";
import { StyledDropZone } from 'react-drop-zone'
import 'react-drop-zone/dist/styles.css'
import SearchField from "react-search-field";

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {"Copyright © "}
      <Link color="inherit" href="https://material-ui.com/">
        Your Website
      </Link>{" "}
      {new Date().getFullYear()}
      {"."}
    </Typography>
  );
}

const useStyles = makeStyles((theme) => ({
  icon: {
    marginRight: theme.spacing(2),
  },
  heroContent: {
    backgroundColor: theme.palette.background.paper,
    padding: theme.spacing(8, 0, 6),
  },
  heroButtons: {
    marginTop: theme.spacing(4),
  },
  cardGrid: {
    paddingTop: theme.spacing(8),
    paddingBottom: theme.spacing(8),
  },
  card: {
    height: "100%",
    display: "flex",
    flexDirection: "column",
  },
  cardMedia: {
    paddingTop: "100%", // 16:9
  },
  cardContent: {
    flexGrow: 1,
  },
  footer: {
    backgroundColor: theme.palette.background.paper,
    padding: theme.spacing(6),
  },
  pagination: {
    float: 'right'
  },
}));

var uploadedfiles = null;

export default function Dashboard() {
  const [popupflag, setPopup] = useState(false);
  const [title, setTitle] = useState();
  const [author, setAuthor] = useState();
  const [langs, setLangs] = useState([]);
  const [booksInfo, setBooksInfo] = useState([]);
  const [dispBooksInfo, setDispBooksInfo] = useState([]);
  const [pageBooksInfo, setPageBooksInfo] = useState([]);
  const [page, setPage] = useState(1);
  const pagination_number = 6;
  const [isAdmin, setAdmin] = useState(false)

  const uploadBook = () => {
    const formData = new FormData();
    console.log(uploadedfiles);    
    formData.append('file', uploadedfiles);
    formData.append('title', title);
    formData.append('author_name', author);
    formData.append('language', langs);

    axios
      .post("http://localhost:4000/user/upload/", formData)
      .then((res) => {
        console.log(res.data);
        alert(res.data.msg);
        window.location.reload()
      })
      .catch((err) => {
        console.log(err);
      });

    setTitle("");
    setAuthor("");
    setLangs([]);
    setPopup(false);
  };

  const cancelUpload = () => {
    setTitle("");
    setAuthor("");
    setLangs([]);
    setPopup(false);
  };

  const deleteBook = (index) => {
      axios.delete('http://localhost:4000/book/deleteBook?filename=' + pageBooksInfo[index].file_name).then(() => {
      alert("Book Deleted Successfully!");
        window.location.reload();
      }).catch(err => {
        console.log(err);
        alert("Could not delete file");
      });
  }

  const viewBook = index => {
    alert(pageBooksInfo[index].file_name);
    window.location = '/viewBook' + '?' + 'book_name=' + pageBooksInfo[index].file_name;
    // localStorage.setItem(booksInfo[index].file_name, "file_name")
    // window.location = 'http://localhost:3000/giga';
  };

  const handleChange = (value, books = dispBooksInfo) => {
    setPageBooksInfo(books.slice(pagination_number * (value - 1), pagination_number * (value)));
  };

  const onSearchClickExample = (value) => {
    var searchBooks = []
    for (var i = 0; i < booksInfo.length; i++) {
      var flag = 0
      if (!(booksInfo[i].title && booksInfo[i].author_name && booksInfo[i].file_name)) {
        flag = 1
      }
      if ((flag == 0) && (booksInfo[i].title.includes(value) || booksInfo[i].author_name.includes(value))) {
        searchBooks.push(booksInfo[i])
      }
    }
    setPage(1)
    setDispBooksInfo(searchBooks)
    handleChange(1, searchBooks)
  }

  useEffect(() => {
    axios.get("http://localhost:4000/user/allbooks")
      .then((res) => {
        setBooksInfo(res.data)
        setDispBooksInfo(res.data)
        handleChange(page)
        setPageBooksInfo(res.data.slice(pagination_number * (page - 1), pagination_number * (page)));
      })
      .catch((err) => {
        console.log(err);
      });
      axios
      .get("http://localhost:4000/user/legit", { withCredentials: true })
      .then((res) => {
        if (!res.data) {
          // window.location = '/login';
        } else {
          // console.log(document.cookie + "this cookie");
          setAdmin(true);
        }
      })
      .catch((err) => {
        console.log(err);
      });
  }, [])

  const classes = useStyles();

  return (
    <Container>
      {pageBooksInfo[0] ?
        <div>
          <AppBar position="relative">
            {
              <SearchField
                placeholder="Search"
                onSearchClick={onSearchClickExample}
              />
            }
          </AppBar>
          {console.log(dispBooksInfo.slice(pagination_number * (page - 1), pagination_number * (page)))}
          {console.log(pageBooksInfo)}
          {/* {console.log(pageBooksInfo[0]["_id"])} */}

          <Container className={classes.cardGrid} maxWidth="md">
            {/* End hero unit */}
            <Grid container spacing={4}>
              {/* {console.log(booksInfo)} */}
              {pageBooksInfo.map((card, ind) => (
                <Grid item key={card._id} xs={10} sm={4} md={4}>
                  <Card className={classes.card}>
                    <CardMedia
                      className={classes.cardMedia}
                      image={"http://localhost:4000/user/thumbnail?filename=" + pageBooksInfo[ind].file_path}
                      title={pageBooksInfo[ind].title}
                    />
                    <CardContent className={classes.cardContent}>

                      <Typography>
                        Title: {pageBooksInfo[ind].title}
                      </Typography>
                      <Typography>
                        Author: {pageBooksInfo[ind].author_name}
                      </Typography>
                      <Typography>
                        Language: {pageBooksInfo[ind].language}
                      </Typography>
                    </CardContent>
                    <CardActions>
                      <Button size="small" color="secondary" onClick={() => { viewBook(ind) }}>
                        View
                    </Button >
                      {isAdmin ?
                        <Button size="small" color="primary" onClick={() => {deleteBook(ind)}}>
                          Delete
                    </Button >
                        : false
                      }
                    </CardActions>
                  </Card>
                </Grid>
              ))}

            </Grid>
            <div className={classes.pagination}>
              <Typography>Page: {page}</Typography>
              {dispBooksInfo.length ?
                <Pagination count={Math.ceil(dispBooksInfo.length / pagination_number)} page={page} onChange={(event, value) => {
                  setPage(value)
                  handleChange(value)
                }
                } /> : <>HENLO</>}
            </div>
          </Container>
          {isAdmin ?
            <footer className={classes.footer}>
              <div>
                {popupflag ? (
                  <>
                    <form onSubmit={uploadBook}>
                      <div className="form-group">
                        <label>Title: </label>
                        <input
                          type="text"
                          className="form-control"
                          onChange={(e) => setTitle(e.target.value)}
                          required
                        />
                      </div>
                      <div className="form-group">
                        <label>Author: </label>
                        <input
                          type="text"
                          className="form-control"
                          onChange={(e) => setAuthor(e.target.value)}
                          required
                        />
                      </div>
                      <Autocomplete
                        freeSolo
                        multiple
                        id="combo-box-demo"
                        options={["Telugu", "Hindi"].map((option) => option)}
                        getOptionLabel={(option) => option}
                        onChange={(event, newValue) => {
                          setLangs(newValue);
                        }}
                        renderInput={(params) => (
                          <TextField
                            {...params}
                            label="Translation Languages"
                            variant="outlined"
                          />
                        )}
                      />
                      <div className="form-group">
                        <input
                          type="submit"
                          value="Upload"
                          className="btn btn-primary"
                        />
                      </div>
                    </form>
                    <form onSubmit={cancelUpload}>
                      <div className="form-group">
                        <input
                          type="submit"
                          value="Cancel"
                          className="btn btn-secondary"
                        />
                      </div>
                    </form>
                  </>
                ) : (
                    <>
                      <StyledDropZone
                        onDrop={(file, event) => {
                          // console.log("onDrop!", file, event);
                          uploadedfiles = file;
                          alert("ok");
                          setPopup(true);
                        }}
                      />
                    </>
                  )}
              </div>
              <Copyright />
            </footer>
            : false
          }
        </div>
        : <>Loading...</>}
    </Container>
  )
}
